mapper.update:
UPDATE shangshufang_customer.university_student_ability_analysis SET
  position_site = #{positionSite,jdbcType=FLOAT}
WHERE student_university_code = #{universityCode,jdbcType=INTEGER}
  AND student_school_id = #{schoolID,jdbcType=INTEGER}
  AND student_id = #{studentID,jdbcType=INTEGER}
  AND technology_id = #{technologyID,jdbcType=INTEGER};

mapper.select:
SELECT COUNT(1) AS total_count
FROM shangshufang_customer.course a
    INNER JOIN shangshufang_customer.course_sign_up b
      ON a.university_code = b.course_university_code
      AND a.school_id = b.course_school_id
      AND a.course_id = b.course_id
      AND b.data_status = 'A'
    INNER JOIN shangshufang_customer.university_student c
      ON b.student_university_code = c.university_code
      AND b.student_school_id = c.school_id
      AND b.student_id = c.student_id
    INNER JOIN shangshufang_base.technology d
        ON a.technology_id = d.technology_id
WHERE 1 = 1

  AND d.direction_id = #{arg0,jdbcType=INTEGER}


  AND d.category_id = #{arg1,jdbcType=INTEGER}


  AND a.technology_id = #{arg2,jdbcType=INTEGER}


  AND b.student_university_code = #{arg3,jdbcType=INTEGER}


  AND b.student_school_id = #{arg4,jdbcType=INTEGER}


  AND a.university_code = #{arg5,jdbcType=INTEGER}


  AND a.school_id = #{arg6,jdbcType=INTEGER}


  AND a.teacher_id = #{arg7,jdbcType=INTEGER}


  AND c.full_name = #{arg8,jdbcType=VARCHAR}
;

SELECT B.analysis_id, B.student_university_code, A.university_name AS student_university_name, B.student_school_id, A.school_name AS student_school_name, a.student_id, A.major_id, A.major_name, A.full_name AS student_name, A.cellphone, A.photo, A.technology_id, A.technology_name, B.knowledge_total_count, B.finish_knowledge_count, B.finish_knowledge_percent, B.finish_exercises_count, B.once_compilation_success_rate, B.once_run_success_rate, B.position_site AS position_site, B.ability_level FROM ( SELECT a.technology_id, g.technology_name, b.student_university_code, d.university_name, b.student_school_id, e.school_name, c.major_id, f.major_name, b.student_id, c.full_name, c.enrollment_year, c.cellphone, c.photo FROM shangshufang_customer.course a INNER JOIN shangshufang_customer.course_sign_up b ON a.university_code = b.course_university_code AND a.school_id = b.course_school_id AND a.course_id = b.course_id AND b.data_status = 'A' INNER JOIN shangshufang_customer.university_student c ON b.student_university_code = c.university_code AND b.student_school_id = c.school_id AND b.student_id = c.student_id INNER JOIN shangshufang_customer.university d ON b.student_university_code = d.university_code INNER JOIN shangshufang_customer.school e ON b.student_university_code = e.university_code AND b.student_school_id = e.school_id INNER JOIN shangshufang_customer.major f ON c.university_code = f.university_code AND c.school_id = f.school_id AND c.major_id = f.major_id INNER JOIN shangshufang_base.technology g ON a.technology_id = g.technology_id WHERE 1 = 1
AND g.direction_id = #{arg2,jdbcType=INTEGER}
AND g.category_id = #{arg3,jdbcType=INTEGER}
AND a.technology_id = #{arg4,jdbcType=INTEGER}
AND b.student_university_code = #{arg5,jdbcType=INTEGER}
AND b.student_school_id = #{arg6,jdbcType=INTEGER}
AND a.university_code = #{arg7,jdbcType=INTEGER}
AND a.school_id = #{arg8,jdbcType=INTEGER}
AND a.teacher_id = #{arg9,jdbcType=INTEGER}
AND c.full_name = #{arg10,jdbcType=VARCHAR}
LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER} ) A INNER JOIN shangshufang_customer.university_student_ability_analysis B ON A.student_university_code = B.student_university_code AND A.student_school_id = B.student_school_id AND A.student_id = B.student_id ORDER BY B.position_site DESC;

SELECT COUNT(DISTINCT knowledge_id) AS TotalCount
FROM shangshufang_customer.university_student_exercises
WHERE student_university_code = #{arg0,jdbcType=INTEGER}
  AND student_school_id = #{arg1,jdbcType=INTEGER}
  AND student_id = #{arg2,jdbcType=INTEGER}
  AND technology_id = #{arg3,jdbcType=INTEGER};
