mapper.select:
SELECT COUNT(1) AS knowledge_total_count
FROM shangshufang_base.technology_knowledge
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
LIMIT 1;

SELECT COUNT(1) AS TotalCount
FROM shangshufang_customer.university_student_ability_analysis
WHERE student_id = #{studentID,jdbcType=INTEGER}
  AND technology_id = #{technologyID,jdbcType=INTEGER};

mapper.update:
UPDATE shangshufang_customer.university_student_ability_analysis
SET finish_knowledge_count   = #{finishedKnowledgeCount,jdbcType=INTEGER},
    finish_knowledge_percent = #{finishedKnowledgePercent,jdbcType=FLOAT},
    standard_score           = #{standardScore,jdbcType=FLOAT},
    ability_level            = #{abilityLevel,jdbcType=VARCHAR}
WHERE student_id = #{studentID,jdbcType=INTEGER}
  AND technology_id = #{technologyID,jdbcType=INTEGER};

mapper.select:
SELECT COUNT(1) AS total_count
FROM shangshufang_customer.university_student_ability_analysis
WHERE technology_id = #{technologyID,jdbcType=INTEGER};

SELECT COUNT(1) AS student_total_count
FROM shangshufang_customer.university_student_ability_analysis
WHERE technology_id = #{technologyID,jdbcType=INTEGER};

SELECT COUNT(1) AS total_count
FROM shangshufang_customer.university_student_ability_analysis
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
  AND standard_score < #{standardScore,jdbcType=DOUBLE};

mapper.update:
UPDATE shangshufang_customer.university_student_ability_analysis
SET position_site = #{positionSite,jdbcType=FLOAT}
WHERE student_id = #{studentID,jdbcType=INTEGER}
  AND technology_id = #{technologyID,jdbcType=INTEGER};

mapper.select:
SELECT COUNT(1) AS total_count
FROM shangshufang_customer.university_student_ability_analysis a
INNER JOIN shangshufang_customer.university_student b
ON a.student_id = b.student_id
INNER JOIN shangshufang_base.technology c
ON a.technology_id = c.technology_id


        c.direction_id = #{directionID,jdbcType=INTEGER}


        AND c.category_id = #{categoryID,jdbcType=INTEGER}


        AND c.technology_id = #{technologyID,jdbcType=INTEGER}


        AND b.university_code = #{universityCode,jdbcType=INTEGER}


        AND b.school_id = #{schoolID,jdbcType=INTEGER}


        AND b.full_name = #{studentName,jdbcType=VARCHAR}


        AND a.student_id != #{studentID,jdbcType=INTEGER}

;

SELECT a.analysis_id, c.university_name AS student_university_name, d.school_name AS student_school_name, b.major_id, e.major_name, a.student_id, b.full_name AS student_name, LEFT(b.enrollment_year, 4) AS enrollment_year, b.cellphone, b.photo, a.technology_id, f.technology_name, a.finish_knowledge_count, a.finish_knowledge_percent, a.position_site, a.standard_score, a.ability_level FROM shangshufang_customer.university_student_ability_analysis a INNER JOIN shangshufang_customer.university_student b ON a.student_id = b.student_id INNER JOIN shangshufang_customer.university c ON b.university_code = c.university_code INNER JOIN shangshufang_customer.school d ON b.university_code = d.university_code AND b.school_id = d.school_id INNER JOIN shangshufang_customer.major e ON b.university_code = e.university_code AND b.school_id = e.school_id AND b.major_id = e.major_id INNER JOIN shangshufang_base.technology f ON a.technology_id = f.technology_id
f.direction_id = #{directionID,jdbcType=INTEGER}
AND f.category_id = #{categoryID,jdbcType=INTEGER}
AND f.technology_id = #{technologyID,jdbcType=INTEGER}
AND b.university_code = #{universityCode,jdbcType=INTEGER}
AND b.school_id = #{schoolID,jdbcType=INTEGER}
AND b.full_name = #{studentName,jdbcType=VARCHAR}
AND a.student_id != #{studentID,jdbcType=INTEGER}
ORDER BY a.position_site DESC LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER};
