mapper.select:
SELECT
COUNT(1) AS total_count
FROM shangshufang_base.code_standard
WHERE 1 = 1

  AND language_id = #{languageID,jdbcType=INTEGER}
;

SELECT A.code_standard_id, A.language_id, B.language_name, A.code_standard_name, C.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, D.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_base.code_standard A INNER JOIN shangshufang_base.programming_language B ON A.language_id = B.language_id INNER JOIN shangshufang_base.system_admin C ON A.create_user = C.admin_id INNER JOIN shangshufang_base.system_admin D ON A.update_user = D.admin_id WHERE 1 = 1
AND A.language_id = #{arg2,jdbcType=INTEGER}
ORDER BY A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
  COUNT(1) AS total_count
FROM shangshufang_base.code_standard
WHERE language_id = #{arg0,jdbcType=INTEGER}
AND code_standard_name = #{arg1,jdbcType=VARCHAR};

mapper.update:
UPDATE shangshufang_base.code_standard
SET code_standard_name = #{codeStandardName,jdbcType=VARCHAR},
    update_user = #{updateUser,jdbcType=VARCHAR}
WHERE language_id = #{languageID,jdbcType=INTEGER}
AND code_standard_id = #{codeStandardID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_base.code_standard
WHERE language_id = #{arg0,jdbcType=INTEGER}
  AND code_standard_id = #{arg1,jdbcType=INTEGER};
