mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.company
WHERE 1=1

  AND province_code = #{arg0,jdbcType=INTEGER}


  AND city_code = #{arg1,jdbcType=INTEGER}


  AND data_status = #{arg2,jdbcType=VARCHAR}
;

SELECT A.company_id, A.company_name, A.company_abbreviation, A.province_code, B.region_name AS province_name, A.city_code, IFNULL(C.region_name, CONCAT(B.region_name, '市')) AS city_name, A.district_code, D.region_name AS district_name, A.address, A.contacts, A.cellphone, A.business_license, A.brand, A.memo, IFNULL(E.using_technology_count,0) AS using_technology_count, IFNULL(F.using_knowledge_count, 0) AS using_knowledge_count, A.data_status, CASE WHEN A.data_status = 'P' THEN '未激活' WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '冻结' ELSE '未知数据状态' END AS data_status_text, G.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, H.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.company A INNER JOIN shangshufang_base.china_region B ON A.province_code = B.region_code LEFT JOIN shangshufang_base.china_region C ON A.city_code = C.region_code INNER JOIN shangshufang_base.china_region D ON A.district_code = D.region_code LEFT JOIN ( SELECT company_id, COUNT(1) AS using_technology_count FROM shangshufang_customer.technology_using GROUP BY company_id ) E ON A.company_id = E.company_id LEFT JOIN ( SELECT company_id, COUNT(1) AS using_knowledge_count FROM shangshufang_customer.technology_knowledge_using GROUP BY company_id ) F ON A.company_id = F.company_id INNER JOIN shangshufang_base.system_admin G ON A.create_user = G.admin_id INNER JOIN shangshufang_base.system_admin H ON A.update_user = H.admin_id WHERE 1=1
AND A.province_code = #{arg2,jdbcType=INTEGER}
AND A.city_code = #{arg3,jdbcType=INTEGER}
AND data_status = #{arg4,jdbcType=VARCHAR}
ORDER BY A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE shangshufang_customer.company SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE company_id = #{companyID,jdbcType=INTEGER};

UPDATE shangshufang_customer.company SET
  brand = #{brand,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE company_id = #{companyID,jdbcType=INTEGER};

UPDATE shangshufang_customer.company SET
  memo = #{memo,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE company_id = #{companyID,jdbcType=INTEGER};
