mapper.select:
SELECT option_id,
exercises_id,
option_text,
is_answer
FROM shangshufang_base.exercise_warehouse_knowledge_choice_option_question


    technology_id = #{technologyID,jdbcType=INTEGER}


    AND knowledge_id = #{knowledgeID,jdbcType=INTEGER}


    AND exercises_id = #{exercisesID,jdbcType=VARCHAR}


ORDER BY option_id;

mapper.delete:
DELETE
FROM shangshufang_base.exercise_warehouse_knowledge_choice_option_question
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
  AND knowledge_id = #{knowledgeID,jdbcType=INTEGER}
  AND exercises_id = #{exercisesID,jdbcType=INTEGER};
