mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.school
WHERE 1=1

  AND university_code = #{universityCode,jdbcType=INTEGER}
;

SELECT A.school_id, A.school_name, A.university_code, B.university_name, A.contacts, A.cellphone, A.data_status, CASE WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '冻结' ELSE '未知数据状态' END AS data_status_text, C.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, D.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.school A INNER JOIN shangshufang_customer.university B ON A.university_code = B.university_code INNER JOIN shangshufang_base.system_admin C ON A.create_user = C.admin_id INNER JOIN shangshufang_base.system_admin D ON A.update_user = D.admin_id WHERE 1=1
AND A.university_code = #{arg2,jdbcType=INTEGER}
ORDER BY A.university_code, A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
  COUNT(1) AS TotalCount
FROM shangshufang_customer.school
WHERE university_code = #{arg0,jdbcType=INTEGER}
AND school_name = #{arg1,jdbcType=VARCHAR};

mapper.update:
UPDATE shangshufang_customer.school SET
  school_name = #{schoolName,jdbcType=VARCHAR},
  contacts = #{contacts,jdbcType=VARCHAR},
  cellphone = #{cellphone,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER};

UPDATE shangshufang_customer.school SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=INTEGER}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_customer.school
WHERE university_code = #{arg0,jdbcType=INTEGER}
AND school_id = #{arg1,jdbcType=INTEGER};
