mapper.select:
SELECT a.technology_id,
       b.technology_name
FROM
    (
        SELECT DISTINCT technology_id
        FROM shangshufang_customer.student_comprehensive_exercises a
        WHERE student_id = #{studentID,jdbcType=INTEGER}
    ) a
        LEFT JOIN shangshufang_base.technology b
                  ON a.technology_id = b.technology_id;

SELECT COUNT(1) AS total_count
FROM shangshufang_customer.comprehensive_exercises_analysis
WHERE student_id = #{studentID,jdbcType=INTEGER}
  AND technology_id = #{technologyID,jdbcType=INTEGER}
  AND result = 'Y';

SELECT COUNT(1) AS total_count FROM shangshufang_customer.student_comprehensive_exercises a LEFT JOIN shangshufang_customer.university_student b ON a.student_id = b.student_id LEFT JOIN shangshufang_customer.university c ON b.university_code = c.university_code LEFT JOIN shangshufang_customer.school d ON b.university_code = d.university_code AND b.school_id = d.school_id LEFT JOIN shangshufang_customer.major e ON b.university_code = e.university_code AND b.school_id = e.school_id AND b.major_id = e.major_id LEFT JOIN shangshufang_base.comprehensive_exercises f ON a.exercises_id = f.exercises_id LEFT JOIN shangshufang_base.programming_language g ON a.program_language = g.language_id WHERE b.cellphone NOT IN ('15353752299', '18182505299', '18100000011', '18100000004', '18100000001', '13000000002', '13000000001','13000000000')
AND a.program_language = #{programLanguage,jdbcType=INTEGER}
AND b.university_code = #{universityCode,jdbcType=INTEGER}
AND b.school_id = #{schoolID,jdbcType=INTEGER}
AND b.major_id = #{majorID,jdbcType=INTEGER}
AND b.full_name = #{fullName,jdbcType=VARCHAR}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR} ;

SELECT a.collection_id, a.student_id, b.full_name, b.university_code, c.university_name, b.school_id, d.school_name, b.major_id, e.major_name, a.exercises_id, f.exam_type, CASE WHEN f.exam_type = 1 THEN '服务端' WHEN f.exam_type = 2 THEN '前端' WHEN f.exam_type = 3 THEN '数据库' WHEN f.exam_type = 4 THEN '全栈' ELSE '其他' END AS exam_type_text, f.exercises_title, f.exam_knowledge, f.exercises_content, f.difficulty_level, CASE WHEN f.difficulty_level = 1 THEN '入门' WHEN f.difficulty_level = 2 THEN '简单' WHEN f.difficulty_level = 3 THEN '中等' WHEN f.difficulty_level = 4 THEN '较难' WHEN f.difficulty_level = 5 THEN '困难' ELSE '未知数据状态' END AS difficulty_level_text, a.program_language, g.language_name, a.git_url, a.review_memo, a.data_status, CASE WHEN a.data_status = 'P' THEN '待批改' WHEN a.data_status = 'Y' THEN '正确' WHEN a.data_status = 'N' THEN '错误' ELSE '未知数据状态' END AS data_status_text, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.student_comprehensive_exercises a LEFT JOIN shangshufang_customer.university_student b ON a.student_id = b.student_id LEFT JOIN shangshufang_customer.university c ON b.university_code = c.university_code LEFT JOIN shangshufang_customer.school d ON b.university_code = d.university_code AND b.school_id = d.school_id LEFT JOIN shangshufang_customer.major e ON b.university_code = e.university_code AND b.school_id = e.school_id AND b.major_id = e.major_id LEFT JOIN shangshufang_base.comprehensive_exercises f ON a.exercises_id = f.exercises_id LEFT JOIN shangshufang_base.programming_language g ON a.program_language = g.language_id WHERE b.cellphone NOT IN ('15353752299', '18182505299', '18100000011', '18100000004', '18100000001', '13000000002', '13000000001', '13000000000')
AND a.program_language = #{programLanguage,jdbcType=INTEGER}
AND b.university_code = #{universityCode,jdbcType=INTEGER}
AND b.school_id = #{schoolID,jdbcType=INTEGER}
AND b.major_id = #{majorID,jdbcType=INTEGER}
AND b.full_name = #{fullName,jdbcType=VARCHAR}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
ORDER BY a.update_time DESC LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER};

SELECT COUNT(1) AS total_count
FROM shangshufang_customer.student_comprehensive_exercises a
LEFT JOIN shangshufang_base.programming_language b
ON a.program_language = b.language_id
LEFT JOIN shangshufang_base.comprehensive_exercises c
ON a.exercises_id = c.exercises_id
WHERE a.student_id = #{studentID, jdbcType=INTEGER}

    AND c.exam_type = #{directionCode,jdbcType=VARCHAR}


    AND a.program_language = #{programLanguage,jdbcType=VARCHAR}


    AND c.difficulty_level = #{difficultyLevelCode,jdbcType=VARCHAR}


    AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
;

SELECT a.collection_id, a.student_id, a.exercises_id, c.exercises_title, c.exam_knowledge, c.exam_type, CASE WHEN c.exam_type = 1 THEN '服务端' WHEN c.exam_type = 2 THEN '前端' WHEN c.exam_type = 3 THEN '数据库' WHEN c.exam_type = 4 THEN '全栈' ELSE '未知数据状态' END AS exam_type_text, c.difficulty_level, CASE WHEN c.difficulty_level = 1 THEN '入门' WHEN c.difficulty_level = 2 THEN '简单' WHEN c.difficulty_level = 3 THEN '中等' WHEN c.difficulty_level = 4 THEN '较难' WHEN c.difficulty_level = 5 THEN '困难' ELSE '未知数据状态' END AS difficulty_level_text, a.program_language, b.language_name AS program_language_name, a.git_url, a.data_status, CASE WHEN A.data_status = 'P' THEN '待批改' WHEN A.data_status = 'Y' THEN '正确' WHEN A.data_status = 'N' THEN '错误' ELSE '未知数据状态' END AS data_status_text, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.student_comprehensive_exercises a LEFT JOIN shangshufang_base.programming_language b ON a.program_language = b.language_id LEFT JOIN shangshufang_base.comprehensive_exercises c ON a.exercises_id = c.exercises_id WHERE a.student_id = #{studentID, jdbcType=INTEGER}
AND c.exam_type = #{directionCode,jdbcType=VARCHAR}
AND a.program_language = #{programLanguage,jdbcType=VARCHAR}
AND c.difficulty_level = #{difficultyLevelCode,jdbcType=VARCHAR}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
ORDER BY a.update_time DESC LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER};

SELECT COUNT(1) AS total_count
FROM shangshufang_customer.student_comprehensive_exercises
WHERE student_id = #{studentID,jdbcType=INTEGER}
  AND exercises_id = #{exercisesID,jdbcType=INTEGER};

mapper.update:
UPDATE shangshufang_customer.student_comprehensive_exercises
SET program_language = #{programLanguage,jdbcType=INTEGER},
    git_url          = #{gitUrl,jdbcType=VARCHAR},
    data_status      = 'P',
    update_user      = #{updateUser,jdbcType=VARCHAR}
WHERE collection_id = #{collectionID,jdbcType=INTEGER};

UPDATE shangshufang_customer.student_comprehensive_exercises
SET data_status = #{dataStatus,jdbcType=VARCHAR},
    review_memo =  #{reviewMemo,jdbcType=VARCHAR},
    update_user = #{updateUser,jdbcType=VARCHAR}
WHERE collection_id = #{collectionID,jdbcType=INTEGER};

mapper.delete:
DELETE
FROM shangshufang_customer.student_comprehensive_exercises
WHERE student_id = #{studentID,jdbcType=INTEGER}
  AND exercises_id = #{exercisesID,jdbcType=INTEGER};
