mapper.select:
SELECT COUNT(1) AS total_count
FROM shangshufang_customer.student_course_exercises a
INNER JOIN shangshufang_customer.course b
    ON a.course_id = b.course_id
INNER JOIN shangshufang_customer.university_student c
    ON a.student_id = c.student_id
INNER JOIN shangshufang_base.technology d
    ON b.technology_id = d.technology_id
INNER JOIN shangshufang_customer.university e
    ON c.university_code = e.university_code


    b.technology_id = #{technologyID,jdbcType=INTEGER}


    AND b.university_code = #{universityCode,jdbcType=INTEGER}


    AND a.school_id = #{schoolID,jdbcType=INTEGER}


    AND a.course_id = #{courseID,jdbcType=INTEGER}


    AND a.student_id = #{studentID,jdbcType=INTEGER}


    AND c.full_name = #{studentName,jdbcType=VARCHAR}


    AND a.data_status = #{dataStatus,jdbcType=VARCHAR}

;

SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.student_course_exercises a
LEFT JOIN shangshufang_customer.university_student b
ON a.student_id = b.student_id


    a.course_id = #{courseID,jdbcType=INTEGER}


    AND a.data_status = #{dataStatus,jdbcType=VARCHAR}


    AND b.full_name = #{studentName,jdbcType=VARCHAR}

;

SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.student_course_exercises


    course_id = #{courseID,jdbcType=INTEGER}


    AND student_id = #{studentID,jdbcType=INTEGER}


    AND data_status = #{dataStatus,jdbcType=VARCHAR}

;

SELECT a.course_exercises_id, d.language_id, d.technology_id, d.technology_name, e.university_code, e.university_name, a.course_id, b.course_name, a.course_class, a.student_id, c.full_name AS student_name, a.data_status, CASE WHEN A.data_status = 'P' THEN '待提交' WHEN A.data_status = 'C' THEN '批改中' WHEN A.data_status = 'R' THEN '待修改' WHEN A.data_status = 'S' THEN '已完成' ELSE '未知数据状态' END AS data_status_text, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.student_course_exercises a INNER JOIN shangshufang_customer.course b ON a.course_id = b.course_id INNER JOIN shangshufang_customer.university_student c ON a.student_id = c.student_id INNER JOIN shangshufang_base.technology d ON b.technology_id = d.technology_id INNER JOIN shangshufang_customer.university e ON c.university_code = e.university_code
b.technology_id = #{technologyID,jdbcType=INTEGER}
AND b.university_code = #{universityCode,jdbcType=INTEGER}
AND a.school_id = #{schoolID,jdbcType=INTEGER}
AND a.course_id = #{courseID,jdbcType=INTEGER}
AND a.student_id = #{studentID,jdbcType=INTEGER}
AND c.full_name = #{studentName,jdbcType=VARCHAR}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
;

SELECT a.course_exercises_id, a.course_id, a.course_class, a.student_id, b.full_name AS student_name, a.data_status, CASE WHEN A.data_status = 'P' THEN '待提交' WHEN A.data_status = 'C' THEN '批改中' WHEN A.data_status = 'R' THEN '待修改' WHEN A.data_status = 'S' THEN '已完成' ELSE '未知数据状态' END AS data_status_text, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.student_course_exercises a LEFT JOIN shangshufang_customer.university_student b ON a.student_id = b.student_id
a.course_id = #{courseID,jdbcType=INTEGER}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
AND b.full_name = #{studentName,jdbcType=VARCHAR}
ORDER BY a.course_class LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER};

SELECT a.course_exercises_id, a.course_id, a.course_class, a.student_id, b.full_name AS student_name, a.data_status, CASE WHEN A.data_status = 'P' THEN '待提交' WHEN A.data_status = 'C' THEN '批改中' WHEN A.data_status = 'R' THEN '待修改' WHEN A.data_status = 'S' THEN '已完成' ELSE '未知数据状态' END AS data_status_text, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.student_course_exercises a LEFT JOIN shangshufang_customer.university_student b ON a.student_id = b.student_id
a.course_id = #{courseID,jdbcType=INTEGER}
AND a.student_id = #{studentID,jdbcType=INTEGER}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
ORDER BY a.course_class LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER};

mapper.update:
UPDATE shangshufang_customer.student_course_exercises
SET data_status = #{dataStatus,jdbcType=VARCHAR},
    update_user = #{updateUser,jdbcType=VARCHAR}
WHERE course_id = #{courseID,jdbcType=INTEGER}
  AND course_class = #{courseClass,jdbcType=INTEGER}
  AND student_id = #{studentID,jdbcType=INTEGER}
  AND course_exercises_id = #{courseExercisesID,jdbcType=INTEGER};
