mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.student_course_exercises a
LEFT JOIN shangshufang_customer.university_student b
ON a.student_id = b.student_id


    a.course_id = #{courseID,jdbcType=INTEGER}


    AND a.data_status = #{dataStatus,jdbcType=VARCHAR}


    AND b.full_name = #{studentName,jdbcType=VARCHAR}

;

SELECT a.course_exercises_id, a.course_id, a.course_class, a.student_id, b.full_name AS student_name, a.data_status, CASE WHEN A.data_status = 'P' THEN '待提交' WHEN A.data_status = 'C' THEN '批改中' WHEN A.data_status = 'R' THEN '修改中' WHEN A.data_status = 'S' THEN '已完成' ELSE '未知数据状态' END AS data_status_text, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.student_course_exercises a LEFT JOIN shangshufang_customer.university_student b ON a.student_id = b.student_id
a.course_id = #{courseID,jdbcType=INTEGER}
AND a.data_status = #{dataStatus,jdbcType=VARCHAR}
AND b.full_name = #{studentName,jdbcType=VARCHAR}
ORDER BY a.course_class LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER};
