mapper.select:
SELECT
COUNT(1) AS total_count
FROM shangshufang_base.technology_category
WHERE 1 = 1

  AND direction_id = #{arg0,jdbcType=INTEGER}


  AND data_status = #{arg1,jdbcType=VARCHAR}
;

SELECT a.technology_category_id, a.technology_category_name, a.direction_id, b.direction_name, a.data_status, CASE WHEN A.data_status = 'P' THEN '待审核' WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '禁用' ELSE '未知数据状态' END AS data_status_text, c.admin_name AS create_user, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, d.admin_name AS update_user, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_base.technology_category a INNER JOIN shangshufang_base.direction b ON a.direction_id = b.direction_id INNER JOIN shangshufang_base.system_admin c ON a.create_user = c.admin_id INNER JOIN shangshufang_base.system_admin d ON a.update_user = d.admin_id WHERE 1 = 1
AND a.direction_id = #{arg2,jdbcType=INTEGER}
AND A.data_status = #{arg3,jdbcType=VARCHAR}
ORDER BY a.direction_id, a.create_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
  COUNT(1) AS total_count
FROM shangshufang_base.technology_category
WHERE direction_id = #{arg0,jdbcType=INTEGER}
AND technology_category_name = #{arg1,jdbcType=VARCHAR};

mapper.update:
UPDATE shangshufang_base.technology_category
SET technology_category_name = #{technologyCategoryName,jdbcType=VARCHAR},
    update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_category_id = #{technologyCategoryID,jdbcType=INTEGER}
AND direction_id = #{directionID,jdbcType=INTEGER};

UPDATE shangshufang_base.technology_category
SET data_status = #{dataStatus,jdbcType=VARCHAR},
    update_user = #{updateUser,jdbcType=INTEGER}
WHERE technology_category_id = #{technologyCategoryID,jdbcType=INTEGER}
  AND direction_id = #{directionID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_base.technology_category
WHERE direction_id = #{arg0,jdbcType=INTEGER}
  AND technology_category_id = #{arg1,jdbcType=INTEGER};
