mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_base.technology_code_standard
WHERE 1=1

  AND technology_id = #{technologyID,jdbcType=INTEGER}
;

SELECT code_standard_id, technology_id, code_standard_name FROM shangshufang_base.technology_code_standard WHERE 1=1
AND technology_id = #{arg2,jdbcType=INTEGER}
ORDER BY update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
  COUNT(1) AS TotalCount
FROM shangshufang_base.technology_code_standard
WHERE technology_id = #{arg0,jdbcType=INTEGER}
AND code_standard_name = #{arg1,jdbcType=VARCHAR};

mapper.update:
UPDATE shangshufang_base.technology_code_standard SET
  code_standard_name = #{codeStandardName,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
AND code_standard_id = #{codeStandardID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_base.technology_code_standard
 WHERE technology_id = #{technologyID,jdbcType=INTEGER}
AND code_standard_id = #{codeStandardID,jdbcType=INTEGER};
