mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_base.technology_knowledge
WHERE 1=1

  AND technology_id = #{arg0,jdbcType=INTEGER}


  AND learning_phase_id = #{arg1,jdbcType=INTEGER}


  AND data_status = #{arg2,jdbcType=VARCHAR}
;

SELECT A.knowledge_id, A.technology_id, B.technology_name, A.learning_phase_id, C.learning_phase_name, A.knowledge_name, A.data_status, CASE WHEN A.data_status = 'P' THEN '未激活' WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '禁用' ELSE '未知数据状态' END AS data_status_text, IFNULL(D.using_company_count, 0) AS using_company_count, E.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, F.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_base.technology_knowledge A INNER JOIN shangshufang_base.technology B ON A.technology_id = B.technology_id INNER JOIN shangshufang_base.learning_phase C ON A.learning_phase_id = C.learning_phase_id LEFT JOIN ( SELECT technology_id, learning_phase_id, knowledge_id, COUNT(1) AS using_company_count FROM shangshufang_customer.technology_knowledge_using WHERE 1=1
AND technology_id = #{arg2,jdbcType=INTEGER}
AND learning_phase_id = #{arg3,jdbcType=INTEGER}
GROUP BY technology_id, learning_phase_id, knowledge_id ) D ON A.technology_id = D.technology_id AND A.learning_phase_id = D.learning_phase_id AND A.knowledge_id = D.knowledge_id INNER JOIN shangshufang_base.system_admin E ON A.create_user = E.admin_id INNER JOIN shangshufang_base.system_admin F ON A.update_user = F.admin_id WHERE 1=1
AND A.technology_id = #{arg2,jdbcType=INTEGER}
AND A.learning_phase_id = #{arg3,jdbcType=INTEGER}
AND A.data_status = #{arg4,jdbcType=VARCHAR}
ORDER BY A.knowledge_id, A.learning_phase_id LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
  COUNT(1) AS TotalCount
FROM shangshufang_base.technology_knowledge
WHERE technology_id = #{arg0,jdbcType=INTEGER}
AND knowledge_name = #{arg1,jdbcType=VARCHAR};

mapper.update:
UPDATE shangshufang_base.technology_knowledge SET
  knowledge_name = #{knowledgeName,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
AND learning_phase_id = #{learningPhaseID,jdbcType=INTEGER}
AND knowledge_id = #{knowledgeID,jdbcType=INTEGER};

UPDATE shangshufang_base.technology_knowledge SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
AND learning_phase_id = #{learningPhaseID,jdbcType=INTEGER}
AND knowledge_id = #{knowledgeID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_base.technology_knowledge
WHERE technology_id = #{arg0,jdbcType=INTEGER}
AND learning_phase_id = #{arg1,jdbcType=INTEGER}
AND knowledge_id = #{arg2,jdbcType=INTEGER};
