mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_base.technology_knowledge
WHERE 1=1

  AND technology_id = #{technologyID,jdbcType=INTEGER}
;

SELECT A.knowledge_id, A.technology_id, B.technology_name, A.knowledge_name, A.data_status, CASE WHEN A.data_status = 'P' THEN '未激活' WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '禁用' ELSE '未知数据状态' END AS data_status_text, C.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, D.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_base.technology_knowledge A INNER JOIN shangshufang_base.technology B ON A.technology_id = B.technology_id INNER JOIN shangshufang_base.system_admin C ON A.create_user = C.admin_id INNER JOIN shangshufang_base.system_admin D ON A.update_user = D.admin_id WHERE 1=1
AND A.technology_id = #{arg2,jdbcType=INTEGER}
ORDER BY A.knowledge_id, A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
  COUNT(1) AS TotalCount
FROM shangshufang_base.technology_knowledge
WHERE technology_id = #{arg0,jdbcType=INTEGER}
AND knowledge_name = #{arg1,jdbcType=VARCHAR};

mapper.update:
UPDATE shangshufang_base.technology_knowledge SET
  knowledge_name = #{knowledgeName,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
AND knowledge_id = #{knowledgeID,jdbcType=INTEGER};

UPDATE shangshufang_base.technology_knowledge SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER}
AND knowledge_id = #{knowledgeID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_base.technology_knowledge
WHERE technology_id = #{arg0,jdbcType=INTEGER}
AND knowledge_id = #{arg1,jdbcType=INTEGER};
