mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_base.technology
WHERE 1 = 1

  AND data_status = #{dataStatus,jdbcType=VARCHAR}
;

SELECT A.technology_id, A.technology_name, A.technology_stars, A.technology_thumbnail, A.technology_memo, A.language_id, E.language_name, A.direction_id, F.direction_name, A.difficulty_level, CASE WHEN A.difficulty_level = 'J' THEN '初级' WHEN A.difficulty_level = 'M' THEN '中级' WHEN A.difficulty_level = 'S' THEN '高级' ELSE '未知数据状态' END AS difficulty_level_text, D.knowledge_count, A.data_status, CASE WHEN A.data_status = 'P' THEN '未激活' WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '禁用' ELSE '未知数据状态' END AS data_status_text, B.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, C.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_base.technology A INNER JOIN shangshufang_base.system_admin B ON A.create_user = B.admin_id INNER JOIN shangshufang_base.system_admin C ON A.update_user = C.admin_id LEFT JOIN ( SELECT technology_id, COUNT(1) AS knowledge_count FROM shangshufang_base.technology_knowledge WHERE data_status = 'A' GROUP BY technology_id ) D ON A.technology_id = D.technology_id INNER JOIN shangshufang_base.programming_language E ON A.language_id = E.language_id LEFT JOIN shangshufang_base.direction F ON A.direction_id = F.direction_id WHERE 1 = 1
AND A.data_status = #{arg2,jdbcType=VARCHAR}
ORDER BY A.create_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT A.technology_id, A.technology_name, A.technology_stars, A.technology_thumbnail, A.technology_memo, A.language_id, B.knowledge_count, E.using_company_count, F.language_name FROM shangshufang_base.technology A LEFT JOIN ( SELECT technology_id, COUNT(1) AS knowledge_count FROM shangshufang_base.technology_knowledge WHERE data_status = 'A' GROUP BY technology_id ) B ON A.technology_id = B.technology_id LEFT JOIN ( SELECT technology_id, COUNT(1) AS using_company_count FROM shangshufang_customer.technology_using A INNER JOIN shangshufang_customer.company B ON A.company_id = B.company_id AND B.data_status = 'A' GROUP BY technology_id ) E ON A.technology_id = E.technology_id INNER JOIN shangshufang_base.programming_language F ON A.language_id = F.language_id WHERE 1 = 1
AND A.data_status = #{arg2,jdbcType=VARCHAR}
ORDER BY A.technology_stars DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT A.technology_id,
    B.technology_name,
    B.technology_thumbnail
FROM
(
    SELECT DISTINCT technology_id
    FROM shangshufang_customer.university_student_exercises
    WHERE student_university_code = #{arg0,jdbcType=INTEGER}
      AND student_school_id = #{arg1,jdbcType=INTEGER}
      AND student_id = #{arg2,jdbcType=INTEGER}
) A
INNER JOIN shangshufang_base.technology B
ON A.technology_id = B.technology_id;

mapper.update:
UPDATE shangshufang_base.technology SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER};

UPDATE shangshufang_base.technology SET
  technology_thumbnail = #{technologyThumbnail,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE technology_id = #{technologyID,jdbcType=INTEGER};
