mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.university_account
WHERE 1=1

  AND university_code = #{arg0,jdbcType=INTEGER}


  AND school_id = #{arg1,jdbcType=INTEGER}
;

SELECT A.account_id, A.university_code, B.university_name, A.school_id, C.school_name, A.cellphone, A.account_role, D.customer_id, D.full_name, D.email, A.data_status, CASE WHEN A.data_status = 'A' THEN '正常' WHEN A.data_status = 'D' THEN '冻结' ELSE '未知数据状态' END AS data_status_text, E.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, F.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM shangshufang_customer.university_account A INNER JOIN shangshufang_customer.university B ON A.university_code = B.university_code INNER JOIN shangshufang_customer.school C ON A.university_code = C.university_code AND A.school_id = C.school_id INNER JOIN shangshufang_customer.university_customer D ON A.university_code = D.university_code AND A.school_id = D.school_id AND A.cellphone = D.cellphone INNER JOIN shangshufang_base.system_admin E ON A.create_user = E.admin_id INNER JOIN shangshufang_base.system_admin F ON A.update_user = F.admin_id WHERE 1=1
AND A.university_code = #{arg2,jdbcType=INTEGER}
AND A.school_id = #{arg3,jdbcType=INTEGER}
ORDER BY A.university_code, A.school_id, A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE shangshufang_customer.university_account SET
  cellphone = #{cellphone,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND account_id = #{accountID,jdbcType=INTEGER};

UPDATE shangshufang_customer.university_account SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=INTEGER}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND account_id = #{accountID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_customer.university_account
WHERE university_code = #{arg0,jdbcType=INTEGER}
AND school_id = #{arg1,jdbcType=INTEGER}
AND account_id = #{arg2,jdbcType=INTEGER};
