mapper.select:
SELECT A.customer_id, A.university_code, B.university_name, A.school_id, C.school_name, A.full_name, A.sex, A.birth, A.cellphone, A.email, A.photo, A.customer_role FROM shangshufang_customer.university_customer A INNER JOIN shangshufang_customer.university B ON A.university_code = B.university_code INNER JOIN shangshufang_customer.school C ON A.university_code = C.university_code AND A.school_id = C.school_id WHERE A.university_code = #{arg0,jdbcType=INTEGER}
AND A.school_id = #{arg1,jdbcType=INTEGER}
AND A.customer_role = #{arg2,jdbcType=VARCHAR} AND A.full_name LIKE #{arg3,jdbcType=VARCHAR} ORDER BY A.customer_id;

mapper.update:
UPDATE shangshufang_customer.university_customer SET
  full_name = #{fullName,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=VARCHAR},
  birth = #{birth,jdbcType=VARCHAR},
  cellphone = #{cellphone,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  photo = #{photo,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND customer_id = #{customerID,jdbcType=INTEGER};

UPDATE shangshufang_customer.university_customer SET
  cellphone = #{cellphone,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND customer_id = #{customerID,jdbcType=INTEGER};

mapper.delete:
DELETE FROM shangshufang_customer.university_customer
WHERE university_code = #{arg0,jdbcType=INTEGER}
AND school_id = #{arg1,jdbcType=INTEGER}
AND customer_id = #{arg2,jdbcType=INTEGER};
