mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM shangshufang_customer.university_student
WHERE 1 = 1

  AND university_code = #{arg0,jdbcType=INTEGER}


  AND school_id = #{arg1,jdbcType=INTEGER}


  AND full_name LIKE #{arg2,jdbcType=VARCHAR}
;

SELECT A.student_id, A.university_code, B.university_name, A.school_id, C.school_name, A.full_name, A.sex, A.birth, A.enrollment_year, A.cellphone, A.email, A.photo, A.is_assistant FROM shangshufang_customer.university_student A INNER JOIN shangshufang_customer.university B ON A.university_code = B.university_code INNER JOIN shangshufang_customer.school C ON A.university_code = C.university_code AND A.school_id = C.school_id WHERE 1 = 1
AND university_code = #{arg2,jdbcType=INTEGER}
AND school_id = #{arg3,jdbcType=INTEGER}
AND full_name LIKE #{arg4,jdbcType=VARCHAR}
ORDER BY A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE shangshufang_customer.university_student SET
  full_name = #{fullName,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=VARCHAR},
  birth = #{birth,jdbcType=VARCHAR},
  enrollment_year = #{enrollmentYear,jdbcType=VARCHAR},
  cellphone = #{cellphone,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  photo = #{photo,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND student_id = #{studentID,jdbcType=INTEGER};

UPDATE shangshufang_customer.university_student SET
  password = #{password,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND student_id = #{studentID,jdbcType=INTEGER};

UPDATE shangshufang_customer.university_student SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND student_id = #{studentID,jdbcType=INTEGER};

UPDATE shangshufang_customer.university_student SET
  is_assistant = #{assistant,jdbcType=BIT},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE university_code = #{universityCode,jdbcType=INTEGER}
AND school_id = #{schoolID,jdbcType=INTEGER}
AND student_id = #{studentID,jdbcType=INTEGER};
